const reviews = [
    {
        id: 1,
        name: "John Smith",
        job: "Web Developer",
        img: "data:image/jpeg;base64,",
        text: "I'm a web developer for Arbitrary Employment, and I'm very happy with the work Rohan has done for me"
    },
    {
        id: 2,
        name: "Jane Smith",
        job: "Frontend Developer",
        img: "data:image/jpeg;base64,",
        text: "I'm a frontend developer for Arbitrary Employment, and I'm very happy with the work Rohan has done for me"
    },
    {
        id: 3,
        name: "Will Smith",
        job: "Backend Developer",
        img: "data:image/jpeg;base64,",
        text: "I'm a Backend developer for Arbitrary Employment, and I'm very happy with the work Rohan has done for me"
    }
]

// Select items
const img = document.getElementById('person-img');
const author = document.getElementById("author");
const job = document.getElementById("job");
const info = document.getElementById("info");

const prevButton = document.querySelector('.prev-btn');
const nextButton = document.querySelector('.next-btn');
const randomButton = document.querySelector('.random-btn');

// Set starting item
let currentItem = 0;

// Load initial item
window.addEventListener('DOMContentLoaded', function (){
    showPerson(currentItem);
});

// Show person based on item
showPerson = (person) => {
    const item = reviews[person];
    img.src = item.img;
    author.textContent = item.name;
    job.textContent = item.job;
    info.textContent = item.text;
}


// Show previous person
prevButton.addEventListener('click', function (){
    currentItem--;
    if (currentItem < 0) {
        currentItem = reviews.length - 1;
    }
    showPerson(currentItem);
})
// Show next person
nextButton.addEventListener('click', function (){
    currentItem++;
    if (currentItem > reviews.length - 1) {
        currentItem = 0;
    }
    showPerson(currentItem);
})

// Show random person
randomButton.addEventListener('click', function (){
    currentItem = Math.floor(Math.random() * reviews.length);
    showPerson(currentItem);
})
